// saved books add a database item
// pull the database items here

import React, { useState, useEffect } from "react"
import { List, ListItem } from "../components/List"
import API from "../util/API"



function Saved () {

    const [books, setBooks] = useState([])

    useEffect(() => {
        API.getBooks.then((res) => {
            setBooks(res)
            console.log("start reading these books brother")
        })
    })

    return(
        <div className="container"> 
        <div>
                <List>
                    {books.map(book => (
                        <ListItem key={book.id}>
                            <div>
                            <strong>
                                {book.title}</strong> by <strong>{book.authors.join(", ")}
                            </strong>
                            </div>
                            <div>
                            <img src={book.image} />
                            </div>
                            <br />
                            <p className="bookDescription">
                                {book.description}
                            </p>
                            <a href={book.link}>Preview Book</a>
                            <br/>
                            <button id="saveBtn" type="primary" >Remove Book</button>
                        </ListItem>
                    ))}
                </List>
                </div>
        </div>
    )
}

export default Saved;
